(1, 1, NULL, 1, 0.000212193, -1, 'iblock', NULL, '\n				SELECT DISTINCT\n					B.*\n					,B.XML_ID as EXTERNAL_ID\n					,DATE_FORMAT(B.TIMESTAMP_X, \'%d.%m.%Y %H:%i:%s\') as TIMESTAMP_X\n					,L.DIR as LANG_DIR\n					,L.SERVER_NAME\n				FROM\n					b_iblock B\n					INNER JOIN b_lang L ON L.LID=B.LID\n				WHERE 1 = 1\n					\n					\n			 ORDER BY  B.SORT asc , B.NAME asc '),
(2, 1, NULL, 2, 0.000304699, -1, 'iblock', NULL, '\n				SELECT DISTINCT\n					B.*\n					,B.XML_ID as EXTERNAL_ID\n					,DATE_FORMAT(B.TIMESTAMP_X, \'%d.%m.%Y %H:%i:%s\') as TIMESTAMP_X\n					,L.DIR as LANG_DIR\n					,L.SERVER_NAME\n				FROM\n					b_iblock B\n					INNER JOIN b_lang L ON L.LID=B.LID\n				WHERE 1 = 1\n					\n					\n			 ORDER BY  B.SORT asc , B.NAME asc '),
(3, 1, NULL, 3, 0.000294686, -1, 'iblock', NULL, '\n				SELECT DISTINCT\n					B.*\n					,B.XML_ID as EXTERNAL_ID\n					,DATE_FORMAT(B.TIMESTAMP_X, \'%d.%m.%Y %H:%i:%s\') as TIMESTAMP_X\n					,L.DIR as LANG_DIR\n					,L.SERVER_NAME\n				FROM\n					b_iblock B\n					INNER JOIN b_lang L ON L.LID=B.LID\n				WHERE 1 = 1\n					\n					\n			 ORDER BY  B.SORT asc , B.NAME asc '),
(4, 1, NULL, 4, 0.000200749, -1, 'clouds', NULL, '\n			SELECT \n			s.ID, s.ACTIVE, s.READ_ONLY, s.SORT, s.SERVICE_ID, s.LOCATION, s.BUCKET, s.SETTINGS, s.CNAME, s.PREFIX, s.FILE_COUNT, s.FILE_SIZE, s.LAST_FILE_ID, s.FILE_RULES\n			FROM\n				b_clouds_file_bucket s\n			\n		\n				ORDER BY\n				SORT DESC, ID ASC\n			'),
(5, 1, NULL, 5, 0.000169039, -1, 'main', NULL, 'SELECT L.*, L.LID as ID, L.LID as LANGUAGE_ID, 	C.FORMAT_DATE, C.FORMAT_DATETIME, C.FORMAT_NAME, C.WEEK_START, C.CHARSET, C.DIRECTION FROM b_language L, b_culture C WHERE C.ID = L.CULTURE_ID  AND (L.LID=\'ru\')  ORDER BY L.SORT '),
(6, 1, NULL, 6, 0.000159979, 0, 'main', NULL, 'SELECT \n	`main_localization_language`.`LID` AS `LID`,\n	`main_localization_language`.`SORT` AS `SORT`,\n	`main_localization_language`.`DEF` AS `DEF`,\n	`main_localization_language`.`ACTIVE` AS `ACTIVE`,\n	`main_localization_language`.`NAME` AS `NAME`,\n	`main_localization_language`.`CULTURE_ID` AS `CULTURE_ID`\nFROM `b_language` `main_localization_language` \n\nWHERE UPPER(`main_localization_language`.`ACTIVE`) like upper(\'Y\')\nORDER BY `main_localization_language`.`SORT` ASC'),
(7, 1, NULL, 7, 0.000231981, -1, 'main', NULL, '\n			SELECT\n				F.ID, F.C_SORT, F.NAME, F.MENU_ID, F.URL, F.MODIFIED_BY, F.CREATED_BY, F.MODULE_ID, F.LANGUAGE_ID,\n				F.COMMENTS, F.COMMON, F.USER_ID, UM.LOGIN AS M_LOGIN, UC.LOGIN as C_LOGIN, U.LOGIN, F.CODE_ID,\n				DATE_FORMAT(F.TIMESTAMP_X, \'%d.%m.%Y %H:%i:%s\')	TIMESTAMP_X,\n				DATE_FORMAT(F.DATE_CREATE, \'%d.%m.%Y %H:%i:%s\')	DATE_CREATE,\n				concat(ifnull(UM.NAME, \'\') , \' \' , ifnull(UM.LAST_NAME, \'\')) as M_USER_NAME,\n				concat(ifnull(UC.NAME, \'\') , \' \' , ifnull(UC.LAST_NAME, \'\')) as C_USER_NAME,\n				concat(ifnull(U.NAME, \'\') , \' \' , ifnull(U.LAST_NAME, \'\')) as USER_NAME\n			FROM\n				b_favorite F\n				LEFT JOIN b_user UM ON (UM.ID = F.MODIFIED_BY)\n				LEFT JOIN b_user UC ON (UC.ID = F.CREATED_BY)\n				LEFT JOIN b_user U ON (U.ID = F.USER_ID)\n			WHERE\n			(1=1\n					AND\n					(\n						(F.USER_ID=1 OR F.COMMON=\'Y\')\n					)\n					\n					AND\n					(\n						F.LANGUAGE_ID = \'ru\'\n					)\n					)\n			 ORDER BY  F.COMMON ASC, F.C_SORT ASC, F.NAME ASC'),
(8, 1, NULL, 8, 0.00044179, -1, 'main', NULL, '\n			SELECT\n				F.ID, F.C_SORT, F.NAME, F.MENU_ID, F.URL, F.MODIFIED_BY, F.CREATED_BY, F.MODULE_ID, F.LANGUAGE_ID,\n				F.COMMENTS, F.COMMON, F.USER_ID, UM.LOGIN AS M_LOGIN, UC.LOGIN as C_LOGIN, U.LOGIN, F.CODE_ID,\n				DATE_FORMAT(F.TIMESTAMP_X, \'%d.%m.%Y %H:%i:%s\')	TIMESTAMP_X,\n				DATE_FORMAT(F.DATE_CREATE, \'%d.%m.%Y %H:%i:%s\')	DATE_CREATE,\n				concat(ifnull(UM.NAME, \'\') , \' \' , ifnull(UM.LAST_NAME, \'\')) as M_USER_NAME,\n				concat(ifnull(UC.NAME, \'\') , \' \' , ifnull(UC.LAST_NAME, \'\')) as C_USER_NAME,\n				concat(ifnull(U.NAME, \'\') , \' \' , ifnull(U.LAST_NAME, \'\')) as USER_NAME\n			FROM\n				b_favorite F\n				LEFT JOIN b_user UM ON (UM.ID = F.MODIFIED_BY)\n				LEFT JOIN b_user UC ON (UC.ID = F.CREATED_BY)\n				LEFT JOIN b_user U ON (U.ID = F.USER_ID)\n			WHERE\n			(1=1\n					AND\n					(\n						( \n					(upper(F.URL) like upper(\'%%repair_db.php%%\') and F.URL is not null)\n					 )\n					)\n					\n					AND\n					(\n						(F.USER_ID=1 OR F.COMMON=\'Y\')\n					)\n					\n					AND\n					(\n						F.LANGUAGE_ID = \'ru\'\n					)\n					)\n			 ORDER BY F.ID DESC'),
