(166, 'b28df22f0dd23fedbcf52def639ea2e3', 'СНАРЯЖЕНИЕ<SPAN>ДЛЯ ПОЛЁТОВ</SPAN>\r\nТОЛЬКО СЕРТИФИЦИРОВАННОЕ НОВОЕ СНАРЯЖЕНИЕ\r\n\rИСПОЛЬЗУЕМОЕ СНАРЯЖЕНИЕ\rКЛУБ “+5” ОБЛАДАЕТ ВСЕМ НЕОБХОДИМЫМ СНАРЯЖЕНИЕМ ДЛЯ ОСУЩЕСТВЛЕНИЯ ПОЛЕТОВ С ПАССАЖИРАМИ И ОБУЧЕНИЯ ПОЛЕТАМ В НАШЕЙ ШКОЛЕ. ВСЕ ОБОРУДОВАНИЕ СЕРТИФИЦИРОВАНО И НАХОДИТСЯ В ХОРОШЕМ СОСТОЯНИИ. КРЫЛЬЯ И ПОДВЕСНЫЕ СИСТЕМЫ РЕГУЛЯРНО ОБНОВЛЯЮТСЯ, Т.К. ОТ НИХ НАПРЯМУЮ ЗАВИСИТ БЕЗОПАСНОСТЬ ПОЛЕТОВ.\rПРОДАЖА И АРЕНДА ПАРАПЛАНЕРНОГО СНАРЯЖЕНИЯ\rДЛЯ САМОСТОЯТЕЛЬНЫХ ПИЛОТОВ МЫ ПРЕДЛАГАЕМ ДЛЯ ПРИОБРЕТЕНИЯ ИЛИ АРЕНДЫ СНАРЯЖЕНИЕ РАЗЛИЧНЫХ ПРОИЗВОДИТЕЛЕЙ: КРЫЛЬЯ, ПОДВЕСКИ, ЗАПАСНЫЕ ПАРАШЮТЫ, ПАРАМОТОРЫ, АКСЕССУАРЫ, ПРИБОРЫ И МНОГОЕ ДРУГОЕ.\rПОДРОБНЕЕ\r\n'),
(167, '3272de4bc657ea2a9e50e52e1ae6e17c', 'МЕСТА<SPAN>ПОЛЕТОВ</SPAN>\r\nГЕОГРАФИЯ НАШИХ ПОЛЕТОВ\r\n\rМЕСТА ПОЛЕТОВ В КАВКАЗСКОМ РЕГИОНЕ\rМЫ ЛЕТАЕМ В РАЗНЫХ МЕСТАХ КАВКАЗСКОГО РЕГИОНА И СТАВРОПОЛЬСКОГО КРАЯ: В ГОРОДЕ ПЯТИГОРСК НА ГОРАХ ЮЦА И МАШУК, НА ИЗВЕСТНОМ ГОРНОЛЫЖНОМ КУРОРТЕ ДОМБАЙ, АРХЫЗ, В КРАСИВЕЙШЕМ ТУРИСТИЧЕСКОМ МЕСТЕ - ЧЕГЕМСКОЕ УЩЕЛЬЕ, НА ЭЛЬБРУСЕ, В КИСЛОВОДСКЕ И ДРУГИХ. КАЖДОЕ ЛЕТНОЕ МЕСТО НЕПОВТОРИМО И ОТЛИЧАЕТСЯ ОТ ВСЕХ ОСТАЛЬНЫХ, ОБЛАДАЕТ СВОЕЙ КРАСОТОЙ, ОСОБЕННОСТЬЮ И МАГИЕЙ. \rВ КАЖДОМ ИЗ ЭТИХ УГОЛКОВ ВЫ СМОЖЕТЕ ОЩУТИТЬ МНОГОГРАННУЮ ВЕЛИЧЕСТВЕННУЮ КРАСОТУ СЕВЕРНОГО КАВКАЗА, НЕПОВТОРИМЫЙ КОЛОРИТ И ОЧАРОВАНИЕ ГОР. \rЧЕГЕМ\rКИСЛОВОДСК\rГОРА ЮЦА, ПЯТИГОРСК\rЭЛЬБРУС\rДОМБАЙ\r‹\r›\r\n'),
(168, 'eec3971baf16d52cfb59abd35b16783d', 'ЦЕНОВАЯ<SPAN>ПОЛИТИКА</SPAN>\r\nНАИЛУЧШЕЕ СОЧЕТАНИЕ ЦЕНЫ И КАЧЕСТВА\r\n\rСТОИМОСТЬ ПОЛЕТОВ НА ПАРАПЛАНЕ\rМЫ НЕ ЭКОНОМИМ НА СНАРЯЖЕНИИ ИЛИ БЕЗОПАСНОСТИ ПОЛЕТОВ, А ИНДИВИДУАЛЬНЫЙ ПОДХОД СТОИТ ВРЕМЕНИ. ПОЭТОМУ СТОИМОСТЬ СКЛАДЫВАЕТСЯ ИЗ НЕСКОЛЬКИХ СОСТАВЛЯЮЩИХ: РАБОТЫ ИНСТРУКТОРА, ИСПОЛЬЗОВАНИЯ СНАРЯЖЕНИЯ, ТРАНСПОРТНЫХ ЗАТРАТ И ОФОРМЛЕНИЯ ЛЁТНЫХ ДОКУМЕНТОВ ПОСЛЕ ОБУЧЕНИЯ.\rЧТО ВХОДИТ В СТОИМОСТЬ ПОЛЕТА\rВ ОЗВУЧЕННУЮ НАМИ СТОИМОСТЬ ВХОДИТ ВСЕ, ОТ ШЛЕМА ДО ЛЕТНЫХ ДОКУМЕНТОВ, И В ПРОЦЕССЕ ПОДГОТОВКИ К ПОЛЕТУ ИЛИ ОБУЧЕНИЯ ЭТА ЦИФРА ОСТАНЕТСЯ НЕИЗМЕННОЙ. БОЛЕЕ ПОДРОБНО О ЦЕНАХ НА ПОЛЕТЫ И ОБУЧЕНИЕ ЧИТАЙТЕ В РАЗДЕЛЕ “ЦЕНЫ”.\r\n'),
